#include <stdio.h>
int ver = 123;
void func1(int ver) {\
    // ver@func1
    ver++;
    // ver@func1 = 1026
    printf("ver = %d\n", ver);
    // ver@func1 is discarded
}
void func2(int *pr) {
    *pr = 1234;
    // *pr@func2(as ver@loop in main) = 1234
    printf("*pr = %d\n", *pr);
    pr = 5678;
    printf("ver = %d\n", ver);
    // ver@global = 123
    // *pr@func2 is discarded, but ver@loop isn't
}
int main() {
    int a = 0;
    int ver = 1025;
    for (int a = 3; a < 4; a++) {
        static int a = 5;
        // a@loop = 5
        printf("a = %d\n", a);
        a = ver;
        // a@loop = 1025
        func1(ver);
        int ver = 7;
        // ver@loop = 7
        printf("ver = %d\n", ver);
        func2(&ver);
        // jump out of loop, and a@loop and ver@loop are discarded
    }
    // a@main = 0, ver@main = 1025
    printf("a = %d\tver = %d\n", a, ver);
}

