#include <stdio.h>
#include <string.h>
char *convert(const char *s) {
    int len = strlen(s);
    char *result = malloc(sizeof(char) * (len + 1));
    strcpy(result, s);
    for (int i = 0; i < len; i++) {
        if (result[i] >= 'A' && result[i] <= 'Z')
            result[i] += 32;
        else if (result[i] >= 'a' && result[i] <= 'z')
            result[i] -= 32;
    }
    return result;
}
int main(void) {
    char *str = "XiyouLinux Group 2022";
    char *temp = convert(str);
    puts(temp);
}

