#include <stdio.h>
long long fibo(int n) {
    if (n == 1 || n == 2) return 1;
    return fibo(n - 1) + fibo(n - 2);
}
int main() {
    int n;
    scanf("%d", &n);
    printf("%lld\n", fibo(n));
}
