typedef struct
{
    int h;
    int m;
    int s;
} time;

void printT(time t)
{
    printf("%02d:%02d:%02d", t.h, t.m, t.s);
}

time nextSec(time t)
{
    if (t.s < 59)
        t.s++;
    else if (t.m < 59)
        t.m++, t.s = 0;
    else if (t.h < 23)
        t.h++, t.m = t.s = 0;
    else
        t.h = t.m = t.s = 0;
    return t;
}

time nextSec2(time t)
{
    if (t.s < 59)
        t.s++;
    else
    {
        t.s = 0;
        if (t.m < 59)
            t.m++;
        else
        {
            t.m = 0;
            if (t.h < 23)
                t.h++;
            else
                t.h = 0;
        }
    }
    return t;
}

time nextSec3(time t)
{
    if (t.s == 59)
        if (t.m == 59)
            if (t.h == 23)
                t.h = t.m = t.s = 0;
            else
                t.h++, t.m = t.s = 0;
        else
            t.m++, t.s = 0;
    else
        t.s++;
    return t;
}

time nextSec4(time t)
{
    if (t.s == 59)
    {
        t.s = 0;
        if (t.m == 59)
        {
            t.m = 0;
            if (t.h == 23)
                t.h = 0;
            else
                t.h++;
        }
        else
            t.m++;
    }
    else
        t.s++;
    return t;
}

int main()
{
    time t;
    getT(&t);
    printT(nextSec(t));
}
