int main() {
    int a = 0;
    int ver = 1025;
    for (int a = 3; a < 4; a++) {
        static int a = 5;
        // a@loop = 5
        printf("a = %d\n", a);
        a = ver;
        // a@loop = 1025
        func1(ver);
        int ver = 7;
        // ver@loop = 7
        printf("ver = %d\n", ver);
        func2(&ver);
        // jump out of loop, and a@loop and ver@loop are discarded
    }
    // a@main = 0, ver@main = 1025
    printf("a = %d\tver = %d\n", a, ver);
}

