for (int i = 1, j, temp; i < entc; i++) {
    temp = enti[i];
    for (j = i;
         // 排序的 condition 很长
         j > 0 && !!(param & P_r) ^ (param & P_t
             ? entv[enti[j - 1]].mtime > entv[temp].mtime
             : strcmp(entv[enti[j - 1]].name, entv[temp].name) > 0);
        j--)
        enti[j] = enti[j - 1];
    enti[j] = temp;
}

